import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Table, Button, Spinner, Alert, Form } from 'react-bootstrap';

// Usa variable de entorno o por defecto http://localhost:3001
const BACKEND_URL = process.env.REACT_APP_BACKEND_URL || 'http://localhost:3001';

function VerDocumentos() {
  const [documentos, setDocumentos] = useState([]);
  const [loading, setLoading] = useState(true);
  const [mensaje, setMensaje] = useState('');
  const [equipos, setEquipos] = useState([]);
  const [equipoId, setEquipoId] = useState(1);

  // Cargar equipos al inicio
  useEffect(() => {
    async function fetchEquipos() {
      try {
        const res = await axios.get('/api/equipos');
        setEquipos(res.data);
        if (res.data.length > 0) setEquipoId(res.data[0].id);
      } catch (error) {
        setMensaje('Error al cargar los equipos');
      }
    }
    fetchEquipos();
  }, []);

  // Cargar documentos cuando cambia el equipo seleccionado
  useEffect(() => {
    if (!equipoId) return;
    async function fetchDocumentos() {
      setLoading(true);
      try {
        const res = await axios.get(`/api/documentos/equipo/${equipoId}`);
        setDocumentos(res.data);
        setMensaje('');
      } catch (error) {
        setMensaje('Error al cargar los documentos');
      }
      setLoading(false);
    }
    fetchDocumentos();
  }, [equipoId]);

  const descargar = (id) => {
    window.open(`${BACKEND_URL}/api/documentos/descargar/${id}`, '_blank');
  };

  const eliminar = async (id) => {
    if (window.confirm('¿Seguro que deseas eliminar este documento?')) {
      try {
        await axios.delete(`${BACKEND_URL}/api/documentos/${id}`);
        setDocumentos(documentos.filter(doc => doc.id !== id));
      } catch (error) {
        setMensaje('Error al eliminar el documento');
      }
    }
  };

  return (
    <div>
      <h3>Documentos de Equipos</h3>
      <Form.Group className="mb-3">
        <Form.Label>Selecciona un equipo:</Form.Label>
        <Form.Select value={equipoId} onChange={e => setEquipoId(Number(e.target.value))}>
          {equipos.map(eq => (
            <option key={eq.id} value={eq.id}>
              {eq.nombre} ({eq.categoria})
            </option>
          ))}
        </Form.Select>
      </Form.Group>
      {mensaje && <Alert variant="danger">{mensaje}</Alert>}
      {loading ? (
        <Spinner animation="border" />
      ) : documentos.length === 0 ? (
        <Alert variant="info">No hay documentos subidos para este equipo.</Alert>
      ) : (
        <Table striped bordered hover>
          <thead>
            <tr>
              <th>ID</th>
              <th>Tipo</th>
              <th>Archivo</th>
              <th>Fecha de subida</th>
              <th>Fecha de vencimiento</th>
              <th>Acciones</th>
            </tr>
          </thead>
          <tbody>
            {documentos.map(doc => (
              <tr key={doc.id}>
                <td>{doc.id}</td>
                <td>{doc.tipo}</td>
                <td>{doc.url_archivo.split('/').pop()}</td>
                <td>{doc.fecha_subida ? doc.fecha_subida.substring(0, 10) : ''}</td>
                <td>{doc.fecha_vencimiento ? doc.fecha_vencimiento.substring(0, 10) : 'N/A'}</td>
                <td>
                  <Button size="sm" variant="success" onClick={() => descargar(doc.id)}>
                    Descargar
                  </Button>{' '}
                  <Button size="sm" variant="danger" onClick={() => eliminar(doc.id)}>
                    Eliminar
                  </Button>
                </td>
              </tr>
            ))}
          </tbody>
        </Table>
      )}
    </div>
  );
}

export default VerDocumentos;
