import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Card, Badge, Alert, Spinner, Row, Col, ListGroup } from 'react-bootstrap';

function Alertas() {
  const [alertas, setAlertas] = useState({
    equiposCalibracion: [],
    documentosVencidos: [],
    equiposSinCalibracion: []
  });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    fetchAlertas();
  }, []);

  const fetchAlertas = async () => {
    try {
      setLoading(true);
      const response = await axios.get('/api/alertas');
      setAlertas(response.data);
      setError('');
    } catch (err) {
      console.error('Error al obtener alertas:', err);
      setError('Error al cargar las alertas');
    } finally {
      setLoading(false);
    }
  };

  const getBadgeVariant = (dias) => {
    if (dias <= 0) return 'danger';
    if (dias <= 7) return 'warning';
    return 'info';
  };

  const formatDate = (dateString) => {
    if (!dateString) return 'N/A';
    return new Date(dateString).toLocaleDateString('es-ES');
  };

  if (loading) {
    return (
      <div className="text-center p-4">
        <Spinner animation="border" role="status">
          <span className="visually-hidden">Cargando...</span>
        </Spinner>
        <p className="mt-2">Cargando alertas...</p>
      </div>
    );
  }

  if (error) {
    return (
      <Alert variant="danger">
        <Alert.Heading>Error</Alert.Heading>
        <p>{error}</p>
      </Alert>
    );
  }

  const totalAlertas = alertas.equiposCalibracion.length + 
                      alertas.documentosVencidos.length + 
                      alertas.equiposSinCalibracion.length;

  return (
    <div>
      <h2 className="mb-4">Panel de Alertas</h2>
      
      {totalAlertas === 0 ? (
        <Alert variant="success">
          <Alert.Heading>¡Excelente!</Alert.Heading>
          <p>No hay alertas pendientes. Todos los equipos están al día.</p>
        </Alert>
      ) : (
        <Row>
          {/* Equipos con calibración próxima */}
          <Col md={4}>
            <Card className="mb-3">
              <Card.Header className="bg-warning text-dark">
                <h5 className="mb-0">
                  Calibración Próxima 
                  <Badge bg="secondary" className="ms-2">
                    {alertas.equiposCalibracion.length}
                  </Badge>
                </h5>
              </Card.Header>
              <Card.Body>
                {alertas.equiposCalibracion.length === 0 ? (
                  <p className="text-muted">No hay equipos con calibración próxima</p>
                ) : (
                  <ListGroup variant="flush">
                    {alertas.equiposCalibracion.map((equipo) => (
                      <ListGroup.Item key={equipo.id} className="d-flex justify-content-between align-items-start">
                        <div>
                          <strong>{equipo.nombre}</strong>
                          <br />
                          <small className="text-muted">
                            {equipo.ubicacion} • {equipo.responsable}
                          </small>
                        </div>
                        <Badge bg={getBadgeVariant(equipo.dias_restantes)}>
                          {equipo.dias_restantes <= 0 ? 'VENCIDA' : `${equipo.dias_restantes} días`}
                        </Badge>
                      </ListGroup.Item>
                    ))}
                  </ListGroup>
                )}
              </Card.Body>
            </Card>
          </Col>

          {/* Documentos vencidos */}
          <Col md={4}>
            <Card className="mb-3">
              <Card.Header className="bg-danger text-white">
                <h5 className="mb-0">
                  Documentos Vencidos
                  <Badge bg="light" text="dark" className="ms-2">
                    {alertas.documentosVencidos.length}
                  </Badge>
                </h5>
              </Card.Header>
              <Card.Body>
                {alertas.documentosVencidos.length === 0 ? (
                  <p className="text-muted">No hay documentos vencidos</p>
                ) : (
                  <ListGroup variant="flush">
                    {alertas.documentosVencidos.map((doc) => (
                      <ListGroup.Item key={doc.id} className="d-flex justify-content-between align-items-start">
                        <div>
                          <strong>{doc.equipo_nombre}</strong>
                          <br />
                          <small className="text-muted">
                            {doc.tipo} • Vencido: {formatDate(doc.fecha_vencimiento)}
                          </small>
                        </div>
                        <Badge bg="danger">
                          {doc.dias_vencido} días vencido
                        </Badge>
                      </ListGroup.Item>
                    ))}
                  </ListGroup>
                )}
              </Card.Body>
            </Card>
          </Col>

          {/* Equipos sin calibración */}
          <Col md={4}>
            <Card className="mb-3">
              <Card.Header className="bg-info text-white">
                <h5 className="mb-0">
                  Sin Calibración Reciente
                  <Badge bg="light" text="dark" className="ms-2">
                    {alertas.equiposSinCalibracion.length}
                  </Badge>
                </h5>
              </Card.Header>
              <Card.Body>
                {alertas.equiposSinCalibracion.length === 0 ? (
                  <p className="text-muted">Todos los equipos tienen calibración reciente</p>
                ) : (
                  <ListGroup variant="flush">
                    {alertas.equiposSinCalibracion.map((equipo) => (
                      <ListGroup.Item key={equipo.id} className="d-flex justify-content-between align-items-start">
                        <div>
                          <strong>{equipo.nombre}</strong>
                          <br />
                          <small className="text-muted">
                            {equipo.ubicacion} • {equipo.responsable}
                          </small>
                        </div>
                        <Badge bg="info">
                          {equipo.dias_sin_calibracion || 'Sin calibración'} días
                        </Badge>
                      </ListGroup.Item>
                    ))}
                  </ListGroup>
                )}
              </Card.Body>
            </Card>
          </Col>
        </Row>
      )}

      <div className="mt-3">
        <button 
          className="btn btn-outline-primary btn-sm"
          onClick={fetchAlertas}
        >
          Actualizar Alertas
        </button>
      </div>
    </div>
  );
}

export default Alertas;
